import { Link } from 'react-router-dom';
import { ArrowLeft, CreditCard, Plus, Trash2, Edit, Save, CheckCircle, AlertCircle } from 'lucide-react';
import { useAccessibility } from '../contexts/AccessibilityContext';
import { paymentMethods as mockPaymentMethods } from '../data/mockData'; // Renombrar para evitar conflicto

const PaymentMethods = () => {
  const { speak } = useAccessibility();
  const [userPaymentMethods, setUserPaymentMethods] = useState([]);
  const [showAddForm, setShowAddForm] = useState(false);
  const [editingMethodId, setEditingMethodId] = useState(null);
  const [newCard, setNewCard] = useState({
    id: '',
    name: '',
    type: 'card', // 'card' o 'paypal' (simulado)
    cardNumber: '',
    expiryDate: '',
    cvv: '',
    isDefault: false,
  });

  useEffect(() => {
    speak('P√°gina de m√©todos de pago. Aqu√≠ puedes a√±adir o gestionar tus formas de pago.');
    // Simular carga de m√©todos de pago guardados
    const savedMethods = JSON.parse(localStorage.getItem('userPaymentMethods')) || [
      { id: 'mock-card-1', name: 'Tarjeta de Cr√©dito', type: 'card', cardNumber: '**** **** **** 1234', expiryDate: '12/25', isDefault: true },
      { id: 'mock-paypal-1', name: 'PayPal', type: 'paypal', email: 'juan.perez@example.com', isDefault: false },
    ];
    setUserPaymentMethods(savedMethods);
  }, [speak]);

  useEffect(() => {
    localStorage.setItem('userPaymentMethods', JSON.stringify(userPaymentMethods));
  }, [userPaymentMethods]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewCard(prev => ({ ...prev, [name]: value }));
  };

  const handleAddMethod = (e) => {
    e.preventDefault();
    if (!newCard.name || (newCard.type === 'card' && (!newCard.cardNumber || !newCard.expiryDate || !newCard.cvv))) {
      speak('Por favor, rellena todos los campos requeridos.');
      return;
    }

    const methodToAdd = {
      ...newCard,
      id: `method-${Date.now()}`,
      cardNumber: newCard.type === 'card' ? `**** **** **** ${newCard.cardNumber.slice(-4)}` : '',
    };

    setUserPaymentMethods(prev => {
      const updatedMethods = prev.map(m => ({ ...m, isDefault: false })); // Desmarcar todos los dem√°s por defecto
      return newCard.isDefault ? [methodToAdd, ...updatedMethods] : [...updatedMethods, methodToAdd];
    });
    
    speak(`${newCard.name} agregado exitosamente.`);
    setNewCard({
      id: '',
      name: '',
      type: 'card',
      cardNumber: '',
      expiryDate: '',
      cvv: '',
      isDefault: false,
    });
    setShowAddForm(false);
  };

  const handleDeleteMethod = (idToDelete, name) => {
    if (window.confirm(`¬øEst√°s seguro de que quieres eliminar ${name}?`)) {
      setUserPaymentMethods(prev => prev.filter(method => method.id !== idToDelete));
      speak(`${name} eliminado.`);
    }
  };

  const handleSetDefault = (idToSetDefault, name) => {
    setUserPaymentMethods(prev =>
      prev.map(method => ({ ...method, isDefault: method.id === idToSetDefault }))
    );
    speak(`${name} establecido como m√©todo de pago predeterminado.`);
  };

  const handleEditClick = (method) => {
    setEditingMethodId(method.id);
    setNewCard({
      ...method,
      cardNumber: method.cardNumber.replace(/\*/g, '').trim(), // Para edici√≥n, quitar los * y permitir reescribir
    });
    setShowAddForm(true);
    speak(`Editando m√©todo de pago: ${method.name}`);
  };

  const handleUpdateMethod = (e) => {
    e.preventDefault();
    setUserPaymentMethods(prev =>
      prev.map(method =>
        method.id === editingMethodId
          ? { 
              ...newCard, 
              id: editingMethodId,
              cardNumber: newCard.type === 'card' ? `**** **** **** ${newCard.cardNumber.slice(-4)}` : '',
            }
          : method
      )
    );
    speak(`${newCard.name} actualizado exitosamente.`);
    setEditingMethodId(null);
    setNewCard({
      id: '',
      name: '',
      type: 'card',
      cardNumber: '',
      expiryDate: '',
      cvv: '',
      isDefault: false,
    });
    setShowAddForm(false);
  };

  return (
    <div className="min-h-screen bg-gray-50 py-8">
      <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Header */}
        <div className="flex items-center space-x-4 mb-8">
          <Link
            to="/account/profile"
            className="btn btn-secondary flex items-center space-x-2"
            onClick={() => speak('Volviendo al perfil')}
            aria-label="Volver a la p√°gina de perfil"
          >
            <ArrowLeft className="h-5 w-5" />
            <span>Mi Perfil</span>
          </Link>
          <h1 className="text-3xl font-bold text-gray-900">M√©todos de Pago</h1>
        </div>

        {/* Lista de m√©todos de pago existentes */}
        <div className="bg-white rounded-lg shadow-lg p-6 mb-8">
          <h2 className="text-xl font-semibold text-gray-900 mb-4">Mis M√©todos de Pago</h2>
          {userPaymentMethods.length === 0 ? (
            <p className="text-gray-600">No tienes m√©todos de pago guardados.</p>
          ) : (
            <div className="space-y-4">
              {userPaymentMethods.map((method) => (
                <div 
                  key={method.id} 
                  className="flex items-center justify-between p-4 border border-gray-200 rounded-lg"
                  role="listitem"
                  aria-label={`M√©todo de pago: ${method.name}. ${method.isDefault ? 'Predeterminado.' : ''}`}
                >
                  <div className="flex items-center space-x-4">
                    <span className="text-3xl">
                      {method.type === 'card' ? 'üí≥' : 'üì±'}
                    </span>
                    <div>
                      <div className="font-medium text-gray-900 flex items-center space-x-2">
                        <span>{method.name}</span>
                        {method.isDefault && (
                          <span className="text-xs bg-blue-100 text-blue-800 px-2 py-1 rounded-full">Predeterminado</span>
                        )}
                      </div>
                      {method.type === 'card' && (
                        <p className="text-sm text-gray-600">{method.cardNumber} {method.expiryDate}</p>
                      )}
                      {method.type === 'paypal' && (
                        <p className="text-sm text-gray-600">{method.email}</p>
                      )}
                    </div>
                  </div>
                  <div className="flex space-x-2">
                    {!method.isDefault && (
                      <button
                        onClick={() => handleSetDefault(method.id, method.name)}
                        className="text-blue-600 hover:text-blue-700 p-2 rounded-full"
                        aria-label={`Establecer como predeterminado ${method.name}`}
                        title="Establecer como predeterminado"
                      >
                        <CheckCircle className="h-5 w-5" />
                      </button>
                    )}
                    <button
                      onClick={() => handleEditClick(method)}
                      className="text-gray-600 hover:text-gray-800 p-2 rounded-full"
                      aria-label={`Editar ${method.name}`}
                      title="Editar m√©todo de pago"
                    >
                      <Edit className="h-5 w-5" />
                    </button>
                    <button
                      onClick={() => handleDeleteMethod(method.id, method.name)}
                      className="text-red-600 hover:text-red-800 p-2 rounded-full"
                      aria-label={`Eliminar ${method.name}`}
                      title="Eliminar m√©todo de pago"
                    >
                      <Trash2 className="h-5 w-5" />
                    </button>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>

        {/* Bot√≥n para a√±adir nuevo m√©todo */}
        {!showAddForm && (
          <button
            onClick={() => {
              setShowAddForm(true);
              setEditingMethodId(null);
              setNewCard({
                id: '',
                name: '',
                type: 'card',
                cardNumber: '',
                expiryDate: '',
                cvv: '',
                isDefault: false,
              });
              speak('Abriendo formulario para a√±adir nuevo m√©todo de pago.');
            }}
            className="btn btn-primary flex items-center justify-center space-x-2 w-full py-3"
            aria-label="A√±adir nuevo m√©todo de pago"
          >
            <Plus className="h-5 w-5" />
            <span>A√±adir Nuevo M√©todo</span>
          </button>
        )}

        {/* Formulario para a√±adir/editar m√©todo de pago */}
        {showAddForm && (
          <div className="bg-white rounded-lg shadow-lg p-6 mt-8">
            <h2 className="text-xl font-semibold text-gray-900 mb-4">
              {editingMethodId ? 'Editar M√©todo de Pago' : 'A√±adir Nuevo M√©todo de Pago'}
            </h2>
            <form onSubmit={editingMethodId ? handleUpdateMethod : handleAddMethod}>
              <div className="mb-4">
                <label htmlFor="name" className="block text-sm font-medium text-gray-700 mb-2">
                  Nombre del M√©todo (ej. "Mi Tarjeta Visa")
                </label>
                <input
                  type="text"
                  id="name"
                  name="name"
                  value={newCard.name}
                  onChange={handleInputChange}
                  className="input"
                  required
                  aria-required="true"
                  aria-label="Nombre del m√©todo de pago"
                />
              </div>

              <div className="mb-4">
                <label htmlFor="type" className="block text-sm font-medium text-gray-700 mb-2">
                  Tipo de M√©todo
                </label>
                <select
                  id="type"
                  name="type"
                  value={newCard.type}
                  onChange={handleInputChange}
                  className="input"
                  aria-label="Seleccionar tipo de m√©todo de pago"
                >
                  <option value="card">Tarjeta de Cr√©dito/D√©bito</option>
                  <option value="paypal">PayPal (Simulado)</option>
                </select>
              </div>

              {newCard.type === 'card' && (
                <div className="space-y-4 mb-4">
                  <div>
                    <label htmlFor="cardNumber" className="block text-sm font-medium text-gray-700 mb-2">
                      N√∫mero de Tarjeta
                    </label>
                    <input
                      type="text"
                      id="cardNumber"
                      name="cardNumber"
                      value={newCard.cardNumber}
                      onChange={handleInputChange}
                      placeholder="**** **** **** ****"
                      className="input"
                      maxLength="19"
                      required
                      aria-required="true"
                      aria-label="N√∫mero de tarjeta de cr√©dito o d√©bito"
                    />
                  </div>
                  <div className="grid grid-cols-2 gap-4">
                    <div>
                      <label htmlFor="expiryDate" className="block text-sm font-medium text-gray-700 mb-2">
                        Fecha de Vencimiento (MM/AA)
                      </label>
                      <input
                        type="text"
                        id="expiryDate"
                        name="expiryDate"
                        value={newCard.expiryDate}
                        onChange={handleInputChange}
                        placeholder="MM/AA"
                        className="input"
                        maxLength="5"
                        required
                        aria-required="true"
                        aria-label="Fecha de vencimiento de la tarjeta en formato mes/a√±o"
                      />
                    </div>
                    <div>
                      <label htmlFor="cvv" className="block text-sm font-medium text-gray-700 mb-2">
                        CVV
                      </label>
                      <input
                        type="text"
                        id="cvv"
                        name="cvv"
                        value={newCard.cvv}
                        onChange={handleInputChange}
                        placeholder="123"
                        className="input"
                        maxLength="4"
                        required
                        aria-required="true"
                        aria-label="C√≥digo de seguridad CVV"
                      />
                    </div>
                  </div>
                </div>
              )}

              {newCard.type === 'paypal' && (
                <div className="mb-4">
                  <label htmlFor="paypalEmail" className="block text-sm font-medium text-gray-700 mb-2">
                    Email de PayPal
                  </label>
                  <input
                    type="email"
                    id="paypalEmail"
                    name="email"
                    value={newCard.email}
                    onChange={handleInputChange}
                    placeholder="tu.email@ejemplo.com"
                    className="input"
                    required
                    aria-required="true"
                    aria-label="Correo electr√≥nico asociado a la cuenta de PayPal"
                  />
                </div>
              )}

              <div className="flex items-center mb-4">
                <input
                  type="checkbox"
                  id="isDefault"
                  name="isDefault"
                  checked={newCard.isDefault}
                  onChange={(e) => setNewCard(prev => ({ ...prev, isDefault: e.target.checked }))}
                  className="h-4 w-4 text-blue-600 focus:ring-blue-500 border-gray-300 rounded"
                  aria-label="Establecer como m√©todo de pago predeterminado"
                />
                <label htmlFor="isDefault" className="ml-2 block text-sm text-gray-900">
                  Establecer como predeterminado
                </label>
              </div>

              <div className="flex justify-end space-x-4">
                <button
                  type="button"
                  onClick={() => {
                    setShowAddForm(false);
                    setEditingMethodId(null);
                    setNewCard({
                      id: '',
                      name: '',
                      type: 'card',
                      cardNumber: '',
                      expiryDate: '',
                      cvv: '',
                      isDefault: false,
                    });
                    speak('Formulario de m√©todo de pago cerrado.');
                  }}
                  className="btn bg-gray-200 text-gray-700 hover:bg-gray-300"
                  aria-label="Cancelar"
                >
                  Cancelar
                </button>
                <button
                  type="submit"
                  className="btn btn-primary"
                  aria-label={editingMethodId ? 'Guardar cambios del m√©todo de pago' : 'A√±adir m√©todo de pago'}
                >
                  {editingMethodId ? 'Guardar Cambios' : 'A√±adir M√©todo'}
                </button>
              </div>
            </form>
          </div>
        )}
      </div>
    </div>
  );
};

export default PaymentMethods;
