import React, { useState, useEffect } from 'react';
import { Mic, MicOff, Volume2, X } from 'lucide-react';
import { useAccessibility } from '../contexts/AccessibilityContext';

const VoiceAssistant = () => {
  const { voiceAssistant, isListening, startListening, stopListening, speak, toggleVoiceAssistant, handleVoiceCommand } = useAccessibility();
  const [showIndicator, setShowIndicator] = useState(false);
  const [lastCommand, setLastCommand] = useState('');
  const [showColorMenu, setShowColorMenu] = useState(false);
  const [colorMode, setColorMode] = useState('normal');

  // Mostrar indicador cuando se está escuchando
  useEffect(() => {
    if (isListening) {
      setShowIndicator(true);
    } else {
      // Ocultar indicador después de un breve delay
      const timer = setTimeout(() => setShowIndicator(false), 2000);
      return () => clearTimeout(timer);
    }
  }, [isListening]);

  // Simular comandos de voz para demostración
  useEffect(() => {
    if (voiceAssistant && !isListening) {
      const commands = [
        'Buscar pizza',
        'Agregar al carrito',
        'Cambiar modo deuteranopia',
        'Subir tamaño de letra',
        'Confirmar pedido',
        'Leer ingredientes',
      ];

      const interval = setInterval(() => {
        const randomCommand = commands[Math.floor(Math.random() * commands.length)];
        setLastCommand(randomCommand);
        speak(`Comando de ejemplo: ${randomCommand}`);
      }, 10000); // Cada 10 segundos

      return () => clearInterval(interval);
    }
  }, [voiceAssistant, isListening, speak]);

  if (!voiceAssistant) return null;

  return (
    <>
      {/* Indicador de escucha flotante */}
      {showIndicator && (
        <div className="fixed top-20 left-1/2 transform -translate-x-1/2 z-50">
          <div className="bg-blue-600 text-white px-6 py-3 rounded-full shadow-lg flex items-center space-x-3 animate-pulse">
            <Mic className="h-5 w-5 animate-pulse" />
            <span className="font-medium">Te escucho...</span>
            <button
              onClick={stopListening}
              className="ml-2 p-1 hover:bg-blue-700 rounded-full transition-colors"
              aria-label="Detener escucha"
            >
              <X className="h-4 w-4" />
            </button>
          </div>
        </div>
      )}

      {/* Panel de asistente de voz */}
      <div className="fixed bottom-6 left-6 z-40 voice-assistant-panel">
        <div className="bg-white rounded-lg shadow-lg p-4 max-w-sm voice-assistant-panel">
          <div className="flex items-center justify-between mb-3">
            <h3 className="text-lg font-semibold text-gray-900 flex items-center">
              <Volume2 className="h-5 w-5 mr-2" />
              Asistente de Voz
            </h3>
            <div className={`w-3 h-3 rounded-full ${isListening ? 'bg-green-500 animate-pulse' : voiceAssistant ? 'bg-gray-400' : 'bg-red-500'}`} />
          </div>
          {/* Botones principales: Escuchar, Desactivar, Color */}
          <div className="flex gap-2 mb-4">
            <button
              onClick={isListening ? stopListening : startListening}
              className={`flex-1 flex items-center justify-center px-4 py-2 rounded-lg font-medium transition-colors ${isListening ? 'bg-red-500 text-white hover:bg-red-600' : 'bg-blue-600 text-white hover:bg-blue-700'}`}
              aria-label={isListening ? 'Detener escucha' : 'Iniciar escucha'}
            >
              {isListening ? (
                <>
                  <MicOff className="h-4 w-4 mr-2" />
                  Detener
                </>
              ) : (
                <>
                  <Mic className="h-4 w-4 mr-2" />
                  Escuchar
                </>
              )}
            </button>
            <button
              onClick={toggleVoiceAssistant}
              className={`flex-1 flex items-center justify-center px-4 py-2 rounded-lg font-medium transition-colors ${voiceAssistant ? 'bg-gray-300 text-gray-700 hover:bg-gray-400' : 'bg-blue-600 text-white hover:bg-blue-700'}`}
              aria-label={voiceAssistant ? 'Desactivar asistente de voz' : 'Activar asistente de voz'}
            >
              <X className="h-4 w-4 mr-2" />
              {voiceAssistant ? 'Desactivar' : 'Activar'}
            </button>
            <button
              onClick={() => setShowColorMenu((prev) => !prev)}
              className="flex-1 flex items-center justify-center px-4 py-2 rounded-lg font-medium transition-colors bg-yellow-400 text-gray-900 hover:bg-yellow-500"
              aria-label="Cambiar modo de color"
            >
              <span className="h-4 w-4 mr-2 rounded-full border border-gray-400 bg-gradient-to-br from-yellow-300 to-yellow-500" />
              Color
            </button>
          </div>
          {/* Menú de selección de color */}
          {showColorMenu && (
            <div className="mb-4 p-3 rounded-lg border border-gray-200 bg-gray-50 flex flex-col gap-2">
              <span className="font-medium text-sm mb-1">Modo de color:</span>
              <button className="btn btn-secondary" onClick={() => { setColorMode('normal'); setShowColorMenu(false); speak('Modo normal activado.'); }}>Normal</button>
              <button className="btn btn-secondary" onClick={() => { setColorMode('deuteranopia'); setShowColorMenu(false); speak('Modo deuteranopia activado.'); }}>Deuteranopia</button>
              <button className="btn btn-secondary" onClick={() => { setColorMode('protanopia'); setShowColorMenu(false); speak('Modo protanopia activado.'); }}>Protanopia</button>
              <button className="btn btn-secondary" onClick={() => { setColorMode('tritanopia'); setShowColorMenu(false); speak('Modo tritanopia activado.'); }}>Tritanopia</button>
              <button className="btn btn-secondary" onClick={() => { setColorMode('high-contrast'); setShowColorMenu(false); speak('Modo alto contraste activado.'); }}>Alto Contraste</button>
            </div>
          )}
          {/* Estado actual y controles solo si está activo */}
          {voiceAssistant && (
            <>
              <div className="mb-4">
                <p className="text-sm text-gray-600 mb-2">
                  Estado: <span className={isListening ? 'text-green-600 font-medium' : 'text-gray-500'}>
                    {isListening ? 'Escuchando' : 'Inactivo'}
                  </span>
                </p>
                {lastCommand && (
                  <div className="bg-blue-50 p-3 rounded-lg">
                    <p className="text-sm text-gray-700">
                      <strong>Último comando:</strong> "{lastCommand}"
                    </p>
                  </div>
                )}
              </div>
              <div className="flex space-x-2">
                <button
                  onClick={isListening ? stopListening : startListening}
                  className={`flex-1 flex items-center justify-center px-4 py-2 rounded-lg font-medium transition-colors ${
                    isListening
                      ? 'bg-red-500 text-white hover:bg-red-600'
                      : 'bg-blue-600 text-white hover:bg-blue-700'
                  }`}
                  aria-label={isListening ? 'Detener escucha' : 'Iniciar escucha'}
                >
                  {isListening ? (
                    <>
                      <MicOff className="h-4 w-4 mr-2" />
                      Detener
                    </>
                  ) : (
                    <>
                      <Mic className="h-4 w-4 mr-2" />
                      Escuchar
                    </>
                  )}
                </button>
              </div>
            </>
          )}
          {/* Comandos rápidos y ayuda */}
          <div className="mt-4">
            <h4 className="text-sm font-medium text-gray-900 mb-2">Comandos rápidos:</h4>
            <div className="grid grid-cols-2 gap-2">
              {[
                'Buscar pizza',
                'Agregar al carrito',
                'Cambiar modo',
                'Ir a promociones',
                'Ir a métodos de pago',
                'Ir a preguntas frecuentes',
                'Ir a soporte de voz',
                'Ir a tutorial de audio',
                'Leer total del carrito',
                'Leer promociones',
                'Repetir',
                'Ayuda'
              ].map((command) => (
                <button
                  key={command}
                  onClick={() => {
                    speak(`Ejecutando: ${command}`);
                    handleVoiceCommand(command);
                  }}
                  className="text-xs bg-gray-100 hover:bg-gray-200 text-gray-700 px-2 py-1 rounded transition-colors"
                  aria-label={`Ejecutar comando: ${command}`}
                >
                  {command}
                </button>
              ))}
            </div>
          </div>
          <div className="mt-4 pt-3 border-t border-gray-200">
            <details className="text-sm">
              <summary className="cursor-pointer text-blue-600 hover:text-blue-700 font-medium">
                Ver todos los comandos
              </summary>
              <div className="mt-2 space-y-1 text-xs text-gray-600">
                <p><strong>Búsqueda:</strong> "Buscar [comida]", "Encontrar [restaurante]"</p>
                <p><strong>Navegación:</strong> "Ir a restaurantes", "Ver carrito", "Ir a inicio", "Ir a promociones", "Ir a métodos de pago", "Ir a preguntas frecuentes", "Ir a soporte de voz", "Ir a tutorial de audio", "Ir a mi cuenta", "Perfil"</p>
                <p><strong>Carrito:</strong> "Agregar [producto]", "Confirmar pedido", "Leer total del carrito"</p>
                <p><strong>Accesibilidad:</strong> "Cambiar modo [tipo]", "Subir/bajar letra", "Activar/desactivar modo alto contraste", "Activar/desactivar modo daltónico"</p>
                <p><strong>Información:</strong> "Leer ingredientes", "Leer descripción", "Leer promociones"</p>
                <p><strong>Utilidad:</strong> "Repetir", "Ayuda", "Comandos disponibles"</p>
              </div>
            </details>
          </div>
        </div>
      </div>

      {/* Indicador de ondas de sonido cuando está escuchando */}
      {isListening && (
        <div className="fixed inset-0 pointer-events-none z-30">
          <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2">
            <div className="flex items-center justify-center space-x-1">
              {[...Array(5)].map((_, i) => (
                <div
                  key={i}
                  className="w-2 h-8 bg-blue-500 rounded-full animate-pulse"
                  style={{
                    animationDelay: `${i * 0.1}s`,
                    animationDuration: '1s'
                  }}
                />
              ))}
            </div>
            <p className="text-center text-blue-600 font-medium mt-4 text-lg">
              Habla ahora...
            </p>
          </div>
        </div>
      )}
    </>
  );
};

export default VoiceAssistant;
